mapper.update:
update pf_job_posting
set
view_count = view_count + 1 where job_posting_no = #{jobPostingNumber}

update pf_job_posting
title=#{title}, content=#{content}, minimum_career=#{minimumCareer}, job=#{job}, year_salary=#{yearSalary}, start_dated=#{startDated}, end_dated=#{endDated}, employment_stat_no=#{employmentStatus.number}, work_place_no=#{district.districtNumber}, minimum_education_no=#{finalEducation.educationNumber}
where job_posting_no=#{jobPostingNumber}
